import math
import numpy as np
import matplotlib.pyplot as plt

def fx (x, m, a) :
    return (1/(a * np.sqrt(2 * math.pi))) * math.exp((-1/2) * ((x -         m)/a)**2)


k = np.linspace(-4, 10, 100)
mt = []
for i in k :
    mt.append(fx(i, 3, 2))


plt.plot(k, mt)
plt.show()
plt.clf()








